// SPDX-License-Identifier: MIT
pragma solidity ^0.7.5;

import "./Blog.sol";
contract TipManager is Blog {
    // input으로 글의 id를 받아서, 글의 tip 개수를 늘린다.
    // 글의 작가의 팁 개수도 늘린다.
    event TipGranted(
        uint tip,
        address owner,
        address payable receiver,
        address author,
        uint tipAmount
    );
    // TODO :  함수에 payable 넣는 버전과 parameter로 tip을 받는 버전 비교
    function giveTip(uint _postId) public payable{
        uint id;
        address owner;
        uint amount;
        (id, owner, , , amount) = getPost(_postId); 
        Post storage post = postList[_postId];
        //post1.tipAmount = post1.tipAmount + msg.value;
        Author storage author = authorList[owner];
        address payable receiver = payable(owner);
        receiver.transfer(msg.value);
        //author.totalTip = author.totalTip + msg.value;
        //authorList[post1.owner] = author;
        emit TipGranted(msg.value, owner, receiver, author.id, amount);
    }   
}
